import react from 'react';
import { StyleSheet, Text, View, Image, FlatList } from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';
import { Title, Card } from 'react-native-paper';
import { MaterialIcons } from '@expo/vector-icons';



const Profile = () => {
    return (
        <View style={style.fullscreen}>
            <LinearGradient
                // Background Linear Gradient
                colors={['#4c669f', '#3b5998', '#192f6a']}
                style={style.background}
            />

            {/* Profile Image  */}
            <View style={style.ProfileVew}>
                <Image
                    style={style.Profileimage}
                    source={{ uri: "https://media.istockphoto.com/photos/happy-student-girl-at-high-school-picture-id1278976856?b=1&k=20&m=1278976856&s=170667a&w=0&h=XmwcmWnFqSRvzrUWdhGY7cAX9Ip9JFDPzeKIm0T4VFo=" }}
                />
            </View>

            {/* Profile Information View  */}


            <View style={style.ProfileVewDetails}>
                <Title>Title</Title>
                <Text style={{ fontSize: 15 }}>Web Developer</Text>
            </View>


            <Card style={style.Details}>
                <View style={style.DetailsView}>
                    <MaterialIcons style={style.icon} name="email" />
                    <Text style={{marginLeft:20}}>gb@gmail.com</Text>
                </View>
            </Card>

            <Card style={style.Details}>
                <View style={style.DetailsView}>
                    <MaterialIcons style={style.icon} name="email" />
                    <Text>gb@gmail.com</Text>
                </View>
            </Card>


            <Card style={style.Details}>
                <View style={style.DetailsView}>
                    <MaterialIcons style={style.icon} name="email" />
                    <Text>gb@gmail.com</Text>
                </View>
            </Card>

            <Card style={style.Details}>
                <View style={style.DetailsView}>
                    <MaterialIcons style={style.icon} name="email" />
                    <Text>gb@gmail.com</Text>
                </View>
            </Card>
        </View>
    )
}


const style = StyleSheet.create({
    fullscreen: {
        flex: 1,
    },
    background: {
        height: "20%",

    },
    Profileimage: {
        height: 140,
        width: 140,
        borderRadius: 70,

    },
    ProfileVew: {
        alignItems: "center",
        marginTop: -70
    },
    ProfileVewDetails: {
        alignItems: "center",
        margin: 5,
        padding: 5
    },
    Details: {
        margin: 5,
        padding:5
    },
    icon: {
        color: "#1a298a",
        fontSize: 25

    },
    DetailsView:{
        flexDirection:"row",
        justifyContent:"space-around",
        padding:5
    }

})

export default Profile
